import React, { Component } from "react";
import { connect } from "react-redux";
import { authAxios, guestAxios } from "../../utils";
import { Link } from "react-router-dom";
import { Navbar, Nav } from "react-bootstrap"
import Account from "../Main/accounts"

class Header extends Component {
    state = {
        account: null
    }

    funcToChangeData = (a) => {
        this.setState({
            account: null,
        });
    };

    render() {
        const { isAuthenticated } = this.props.auth
        return <div>
            {this.state.account && (
                <Account
                    funcToChange={this.funcToChangeData}
                />
            )}
            <Navbar bg="light" expand="lg">
                <Navbar.Brand href="#home">Our Stores</Navbar.Brand>
                <Navbar.Toggle aria-controls="basic-navbar-nav" />
                <Navbar.Collapse id="basic-navbar-nav">
                    <Nav className="mr-auto">
                        {isAuthenticated ? <Link to="/new"><Nav.Link>New Store</Nav.Link></Link> :
                            <Nav.Link onClick={() => this.setState({
                                account: 1
                            })}>Login</Nav.Link>}
                    </Nav>
                </Navbar.Collapse>
            </Navbar>
        </div>
    }
}

const mapStateToProps = (state) => ({
    isAuthenticated: state.auth.isAuthenticated,
    auth: state.auth,
});

export default connect(mapStateToProps)(Header);
