import React, { Component } from "react";
import { connect } from "react-redux";
import { authAxios, guestAxios } from "../../../utils";
import { Link } from "react-router-dom";
import { Row, Col } from "reactstrap"
import { Button } from '@material-ui/core';

import { getStore } from "../../../constants"


class DetailPage extends Component {
    state = {
        data: this.props.location.state.data
    }
    componentDidMount() {
        console.log(this.state.data)
    }

    render() {
        const { data } = this.state
        return <div>
            <img src={`http://127.0.0.1:8000` + data.coverPhoto} style={{ width: "100%", height: "600px" }} />
            <br />
            <br />
            <div>
                <Row style={{ width: "90%", marginLeft: "5%" }}>
                    <Col className="mb-5 mb-lg-0" lg="6" md="6">
                        <h1
                            className="gradient-default"
                            style={{
                                fontFamily: "Rouge Script",
                                color: "#0086b3",
                                fontWeight: "bold",
                            }}
                        >
                            Our Clinic
                  </h1>
                        <div
                            style={{
                                width: 90,
                                height: 3,
                                backgroundColor: "#0086b3",
                            }}
                        ></div>
                        <br />
                        <h4 style={{ fontFamily: "Great Vibes", color: "#0e1b30" }}>
                            Rishi nagar, near Sewak Sabha Hospital, <br />
                    Haryana 125001
                  </h4>
                        <br />
                        <div
                            style={{
                                color: "#11cdef",
                                fontSize: 23,
                                cursor: "pointer",
                            }}
                        >
                            <i className="fa fa-location-arrow"></i> Get Directions
                  </div>
                        <br />
                        <div
                            style={{
                                color: "#11cdef",
                                fontSize: 23,
                                cursor: "pointer",
                            }}
                        >
                            <i className="fa fa-phone"></i> Call +91 9068085544
                  </div>
                        <br />
                        <div
                            style={{
                                color: "#11cdef",
                                fontSize: 23,
                                cursor: "pointer",
                            }}
                        >
                            <i className="fa fa-inbox"></i> Text +91 9068085544
                  </div>
                    </Col>
                    <Col className="mb-5 mb-lg-0" lg="6" md="6">
                        <h1
                            style={{
                                fontFamily: "Rouge Script",
                                color: "#0086b3",
                                fontWeight: "bold",
                            }}
                        >
                            Store Hours
                  </h1>
                        <div
                            style={{
                                width: 90,
                                height: 3,
                                backgroundColor: "#0086b3",
                            }}
                        ></div>
                        <br />
                        <Row>
                            <Col>
                                <h4
                                    style={{
                                        color: "#1a174d",
                                        fontFamily: "Great Vibes",
                                    }}
                                >
                                    Monday
                        <br />
                        Tuesday <br /> Wednesday <br />
                        Thursday <br />
                        Friday <br /> Saturday
                      </h4>
                            </Col>
                            <Col>
                                <h4
                                    style={{
                                        color: "#1a174d",
                                        fontFamily: "Great Vibes",
                                    }}
                                >
                                    7AM - 7PM
                        <br />
                        7AM - 7PM <br /> 7AM - 7PM <br />
                        7AM - 7PM <br />
                        7AM - 7PM <br /> 7AM - 7PM
                      </h4>
                            </Col>
                        </Row>
                    </Col>
                </Row>

            </div>
        </div>
    }
}

const mapStateToProps = (state) => ({
    isAuthenticated: state.auth.isAuthenticated,
    auth: state.auth,
});

export default connect(mapStateToProps)(DetailPage);
