import React, { Component } from "react";
import { connect } from "react-redux";
import { authAxios, guestAxios } from "../../utils";
import { Link } from "react-router-dom";
import { TextField, Button } from '@material-ui/core';
import { Row, Col } from "reactstrap"
import StoreHours from "./newStore/storeHours"

class NewStore extends Component {
    state = {
        data: {
            name: "",
            about: "",
            lon: "",
            lat: ""
        },
        days: [
            "Monday",
            "Tuesday",
            "Wednesday",
            "Thrusday",
            "Friday",
            "Saturday",
            "Sunday"
        ]
    }

    handleChange = (e) => {
        const data1 = this.state.data[e.target.name]

        console.log(data1)
        this.setState({
            data: this.state.data
        })
    }

    render() {
        const { days, data } = this.state
        const { name, about, lon, lat } = data

        return <div className="container">
            <br />
            <h3>Add Store</h3>
            <br />
            <TextField fullWidth value={name} onChange={this.handleChange} name="name" label="Name" variant="outlined" />
            <br />
            <br />
            <TextField fullWidth value={about} label="About Us" variant="outlined" />
            <br />
            <br />
            <Row>
                <Col><TextField value={lat} fullWidth label="Latitude" variant="outlined" /></Col>
                <Col><TextField fullWidth value={lon} label="Longitude" variant="outlined" /></Col>
            </Row>
            <br />
            <h4>Store Hours</h4>
            <br />
            {days.map(item => <StoreHours day={item} />)}
            <div style={{ textAlign: "center" }}>
                <Button variant="contained" color="primary">
                    Create
                </Button>
            </div>

            <br />
            <br />
        </div>
    }
}

const mapStateToProps = (state) => ({
    isAuthenticated: state.auth.isAuthenticated,
    auth: state.auth,
});

export default connect(mapStateToProps)(NewStore);
