import React, { Component } from "react";
import { connect } from "react-redux";
import { authAxios } from "../../utils";
import { TextField, Button } from '@material-ui/core';
import { Row, Col } from "reactstrap"
import StoreHours from "./newStore/storeHours"
import { createStore } from "../../constants"
import Snackbar from '@material-ui/core/Snackbar';
import MuiAlert from '@material-ui/lab/Alert';
import CameraAltIcon from "@material-ui/icons/CameraAlt";
import { withStyles } from "@material-ui/core/styles";

function Alert(props) {
    return <MuiAlert elevation={6} variant="filled" {...props} />;
}

class NewStore extends Component {
    state = {
        name: "",
        about: "",
        lon: "",
        lat: "",
        error: "",
        open: false,
        days: [
            "Monday",
            "Tuesday",
            "Wednesday",
            "Thrusday",
            "Friday",
            "Saturday",
            "Sunday"
        ]
    }

    onSubmit = () => {
        const { name, about, lon, lat } = this.state
        if (name && about && lon && lat) {
            const body = {
                name: name,
                about: about,
                lon: lon,
                lat: lat,
                data: this.props.storeTime.storeTime
            }
            authAxios(this.props.auth.token).post(`${createStore}`, body).then(res => this.setState({
                name: "",
                about: "",
                lon: "",
                lat: "",
            }))
        }
        else {
            this.setState({
                open: true,
                error: "Fill all required fields."
            })
        }
    }

    handleClose = () => {
        this.setState({
            error: "",
            open: false
        })
    }

    render() {
        const { days, name, about, lon, lat, open, error } = this.state


        return <div className="container">
            <Snackbar open={open} autoHideDuration={6000} onClose={this.handleClose}>
                <Alert severity="error" onClose={this.handleClose}>
                    {error}
                </Alert>
            </Snackbar>
            <br />
            <h3>Add Store</h3>
            <br />
            <TextField fullWidth value={name} onChange={(e) => this.setState({ name: e.target.value })} name="name" label="Name" variant="outlined" />
            <br />
            <br />
            <TextField fullWidth value={about} onChange={(e) => this.setState({ about: e.target.value })} label="About Us" variant="outlined" />
            <br />
            <br />
            <Row>
                <Col><TextField value={lat} onChange={(e) => this.setState({ lat: e.target.value })} fullWidth label="Latitude" variant="outlined" /></Col>
                <Col><TextField fullWidth value={lon} onChange={(e) => this.setState({ lon: e.target.value })} label="Longitude" variant="outlined" /></Col>
            </Row>
            <br />
            <h4>Store Hours</h4>
            <br />
            {days.map(item => <StoreHours day={item} />)}

            <div style={{ textAlign: "center" }}>
                <div>

                    <div className={classes.upload}>
                        <div className={classes.addImages}>
                            <CameraAltIcon />
                            <div>
                                <input
                                    id="kmImage"
                                    className={classes.input}
                                    type="file"
                                    onChange={this.kmImageChange}
                                    accept="image/*"
                                    capture="camera"
                                />
                                <label
                                    for="kmImage"
                                    className={classes.customfileupload}
                                >
                                    <p>Add images</p>
                                </label>
                            </div>
                        </div>
                    </div>
                </div>
                <Button variant="contained" onClick={this.onSubmit} color="primary">
                    Create
                </Button>
            </div>
            <br />
            <br />
        </div>
    }
}

const mapStateToProps = (state) => ({
    isAuthenticated: state.auth.isAuthenticated,
    auth: state.auth,
    storeTime: state.storeTime,
});


export default connect(mapStateToProps, {})(withStyles(styles)(NewStore));
